clear all;
close all;

% 設定資料夾路徑和檔案名稱格式
folderPath = 'C:\Users\sgrc - 325\Desktop\DAS data\';
baseFileName = 'demod_data_2025-07-30_10-43-02_';
fileExtension = '.txt';

% 初始變數
DASdata = [];
startx = 0;
endx = 141;
totalFiles = endx - startx + 1;

% 建立進度條
hWait = waitbar(0, '正在讀取檔案...');

for idx = 1:totalFiles
    i = startx + idx - 1;

    % 更新進度條
    waitbar(idx / totalFiles, hWait, sprintf('讀取檔案中 (%d/%d)', idx, totalFiles));

    fileName = [baseFileName, num2str(i), fileExtension];
    fullPath = fullfile(folderPath, fileName);
    
    if exist(fullPath, 'file')
        data = load(fullPath);
        DASdata = [DASdata; data];  % 串接資料（垂直合併）
    else
        fprintf('\n檔案不存在: %s\n', fullPath);
    end
end

% 關閉進度條
close(hWait);

disp('所有檔案已成功串接。'); %[output:8a58eafb]
%%

% 繪圖
figure; %[output:8270bb21]
plot(DASdata(:,812)) %[output:8270bb21]
xlabel('sample (S)'); %[output:8270bb21]
ylabel('amplitude (rad)'); %[output:8270bb21]
title('The signal of point 812'); %[output:8270bb21]
grid on; %[output:8270bb21]


